sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	var ga = new GlideAjax('TestGetAPIandDisplay');


	ga.addParam('sysparm_name','getAddress');
	ga.addParam('sysparm_pernr', g_form.getValue("u_pernr"));

	ga.getXML(getCategoriesOutput);

	function getCategoriesOutput(response) {


		 var answer = response.responseXML.documentElement.getAttribute("answer");
		var obj = JSON.parse(answer);

	   alert(obj);


		g_form.setValue('u_city',obj.city);
		g_form.setValue('u_country',obj.country);
		g_form.setValue('u_addr_no',obj.ip);
		g_form.setValue('u_portal',obj.postal);


	   }






}
